import {usesState} from 'react'


export default function Accordian(props){
    const[expandedIndex, setExpandedIndex] = userState(0)


    const {items} = props
    const renderedItems = items.map((item, index)=>{
        const isExpanded = index === expandedIndex
        return (
            <div key ={item.id}>
                <div className= "flex justify-between p-3 bg-gray-100 border-b items-center cursor-pointer">
                    {item.label}</div>
                {/*If the content index matches the expanded index in state,  */}
                <div>{item.content}</div>
            </div>
        )
    })

    return (<div>
        {renderedItems}
    </div>)
}
