// pages/AlertPage.js
import React, { useState } from 'react';
import Alert from '../components/Alert';

const AlertPage = () => {
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  const triggerAlert = (message, type) => {
    setAlertMessage(message);
    setAlertType(type);

    // Clear the alert after 3 seconds
    setTimeout(() => {
      setAlertMessage('');
      setAlertType('');
    }, 3000);
  };

  const buttonStyle = {
    padding: '10px 20px',
    margin: '0 10px',          // Margin between buttons
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  const pageStyle = {
    display: 'flex',            // Flexbox
    flexDirection: 'column',    // Stack elements vertically
    justifyContent: 'center',   // Center vertically
    alignItems: 'center',       // Center horizontally
    height: '100vh',            // Full viewport height
    textAlign: 'center',        // Center the text
  };

  const buttonContainerStyle = {
    display: 'flex',            // Buttons side by side
    justifyContent: 'center',   // Center buttons horizontally
    marginTop: '20px',          // Space above buttons
  };

  return (
    <div style={pageStyle}>
      <h1>Alert Page</h1>

      <div style={buttonContainerStyle}>
        <button
          style={buttonStyle}
          onClick={() => triggerAlert('Success! This is an alert message.', 'success')}
        >
          Trigger Success Alert
        </button>

        <button
          style={buttonStyle}
          onClick={() => triggerAlert('Error! Something went wrong.', 'error')}
        >
          Trigger Error Alert
        </button>
      </div>

      {alertMessage && <Alert message={alertMessage} type={alertType} />}
    </div>
  );
};

export default AlertPage;
