// pages/AlertPage.js
import React, { useState } from 'react';
import Alert from '../components/Alert';

const AlertPage = () => {
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  const triggerAlert = (message, type) =>  {
    setAlertMessage(message);
    setAlertType(type);

    // Clear the alert after 3 seconds
    setTimeout(() => {
      setAlertMessage('');
      setAlertType('');
    }, 3000);
  };

  const buttonStyle = {
    padding: '10px 20px',
    margin: '10px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  return (
    <div>
      <h1>Press A Button</h1>

      <button
        style={buttonStyle}
        onClick={() => triggerAlert('Success! This is an alert message.', 'success')}
      >
        Are You Right?
      </button>

      <button
        style={buttonStyle}
        onClick={() => triggerAlert('Error! Something went wrong.', 'error')}
      >
        Or Are You Wrong?
      </button>

      {alertMessage && <Alert message={alertMessage} type={alertType} />}
    </div>
  );
};

export default AlertPage;
