// pages/AlertPage.js
import React, { useState } from 'react';
import Alert from '../components/Alert';

const AlertPage = () => {
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  const triggerAlert = (message, type) => {
    setAlertMessage(message);
    setAlertType(type);

    setTimeout(() => {
      setAlertMessage('');
      setAlertType('');
    }, 3000);  
  };

  const closeAlert = () => {
    setAlertMessage('');
    setAlertType('');
  };

  const buttonStyle = {
    padding: '10px 20px',
    margin: '0 10px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  const pageStyle = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    textAlign: 'center',
  };

  const buttonContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '20px',
  };

  return (
    <div style={pageStyle}>
      <h1>Alert Page</h1>

      {/* <div style={buttonContainerStyle}>
        <button
          style={buttonStyle}
          onClick={() => triggerAlert('One of the buttons can be right :)', 'success')}
        >
          Red Pill
        </button>

        <button
          style={buttonStyle}
          onClick={() => triggerAlert('Or it can be wrong :(', 'error')}
        >
          Blue Pill
        </button>
      </div>

      {alertMessage && <Alert message={alertMessage} type={alertType} onClose={closeAlert} />}
    </div> */}

    <div className="flex space-x-4"> {/* Flexbox for buttons side by side with spacing */}
        <button
          className="bg-red-500 text-white py-2 px-4 rounded"
          onClick={() => triggerAlert('Success! This is an alert message.', 'success')}
        >
          Success Button
        </button>

        <button
          className="bg-blue-500 text-white py-2 px-4 rounded"
          onClick={() => triggerAlert('Error! Something went wrong.', 'error')}
        >
          Error Button
        </button>
      </div>

      {alertMessage && <Alert message={alertMessage} type={alertType} />}
    </div>
  );
};

export default AlertPage;
