import Button from '../components/Button'
import Modal from '../components/Modal'

export default function ModalPage(){
    const [isModalOpen, setIsModalOpen] = useState(true)
    const handleClick = () => {
        setIsModalOpen(true)
    }

    const handleClose = ()=> {
        setIsModalOpen(false)
    }


    const modal = 
        (<Modal onClose ={handleClose} actionBar={actionBar}>
            This is a very important modal for you to accept or decline.
        </Modal>)

    const actionBar = (
        <>
            <Button danger onClick={()=>console.log('click the danger button')}>Delete</Button>
            <Button success onCLick={handleClose}>Accept</Button>
        </>)

    return( 

        <div className= 'relative w-96 overflow-hidden' style={{height: '100px', width:'100px'}}>
        <Button primary outline className="m-4" onClick= {handleClick}>
            Open Modal
        </Button>
        <Button danger outline className="m-4" onClick= {handleClose}>
            Close Modal
        </Button>

        {isModalOpen && modal}
        </div>
    )

}
